mapper.update:
update buybook
set stu_id = #{stuId,jdbcType=CHAR},
  course_id = #{courseId,jdbcType=CHAR},
  book_id = #{bookId,jdbcType=INTEGER},
  semester_id = #{semesterId,jdbcType=CHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, stu_id, course_id, book_id, semester_id
from buybook
where id = #{id,jdbcType=INTEGER}
